import React from 'react';
import {View, Image, Text, FlatList,TouchableOpacity, TextInput} from 'react-native'
import styles from './styles';

const SingleProductEditScreen=(props)=>{
    const product=props.navigation.getParam('product')
    console.log('props in edit page : ',product);

    const renderProductImage=({item})=>{
        return (
            <TouchableOpacity>
                <Image style={[styles.photo, {margin:10}]} source={{ uri: item }} />
            </TouchableOpacity>
        )
    }


    return (
        <View style={styles.container}>
            <TouchableOpacity>
                <Image style={[styles.photo, {margin:10}]} source={{ uri: product.photo_url }} />
            </TouchableOpacity>

            <TextInput style={styles.productTitle}>
                {product.title}
            </TextInput>
            {
                product.photosArray.length>0? (
                    <FlatList
                    vertical
                    showsVerticalScrollIndicator={true}
                    numColumns={2}
                    data={product.photosArray}
                    renderItem={renderProductImage}
                    keyExtractor={item => `${item.recipeId}`}
            />
                ):null
            }

        </View>
    )
}

export default SingleProductEditScreen
